        },
    }

import json from typing import List
from fastapi import APIRouter, Depends, FastAPI, WebSocket from fastapi.testclient import TestClient from typing_extensions import Annotated
def dependency_list() -> List[str]: return []
DepList = Annotated[List[str], Depends(dependency_list)]
def create_dependency(name: str): def fun(deps: DepList): deps.append(name)
return Depends(fun)
router = APIRouter(dependencies=[create_dependency("router")]) prefix_router = APIRouter(dependencies=[create_dependency("prefix_router")]) app = FastAPI(dependencies=[create_dependency("app")])
@app.websocket("/", dependencies=[create_dependency("index")]) async def index(websocket: WebSocket, deps: DepList): await websocket.accept() await websocket.send_text(json.dumps(deps)) await websocket.close()
@router.websocket("/router", dependencies=[create_dependency("routerindex")]) async def routerindex(websocket: WebSocket, deps: DepList): await websocket.accept() await websocket.send_text(json.dumps(deps)) await websocket.close()
@prefix_router.websocket("/", dependencies=[create_dependency("routerprefixindex")]) async def routerprefixindex(websocket: WebSocket, deps: DepList): await websocket.accept() await websocket.send_text(json.dumps(deps)) await websocket.close()
app.include_router(router, dependencies=[create_dependency("router2")]) app.include_router( prefix_router, prefix="/prefix", dependencies=[create_dependency("prefix_router2")] )
def test_index(): client = TestClient(app) with client.websocket_connect("/") as websocket: data = json.loads(websocket.receive_text()) assert data == ["app", "index"]
def test_routerindex(): client = TestClient(app) with client.websocket_connect("/router") as websocket: data = json.loads(websocket.receive_text()) assert data == ["app", "router2", "router", "routerindex"]
def test_routerprefixindex(): client = TestClient(app) with client.websocket_connect("/prefix/") as websocket: data = json.loads(websocket.receive_text()) assert data == ["app", "prefix_router2", "prefix_router", "routerprefixindex"]

from typing import Optional

from fastapi import Depends, FastAPI
from pydantic import BaseModel, validator

app = FastAPI()


class ModelB(BaseModel):
    username: str


class ModelC(ModelB):
    password: str


class ModelA(BaseModel):
    name: str
    description: Optional[str] = None
    model_b: ModelB

    @validator("name")
    def lower_username(cls, name: str, values):
        if not name.endswith("A"):
            raise ValueError("name must end in A")
        return name


async def get_model_c() -> ModelC:
    return ModelC(username="test-user", password="test-password")


@app.get("/model/{name}", response_model=ModelA)
async def get_model_a(name: str, model_c=Depends(get_model_c)):
    return {"name": name, "description": "model-a-desc", "model_b": model_c}

import pytest
from fastapi.exceptions import ResponseValidationError
from fastapi.testclient import TestClient

from ..utils import needs_pydanticv1


@pytest.fixture(name="client")
def get_client():
    from .app_pv1 import app

    client = TestClient(app)
    return client


@needs_pydanticv1
def test_filter_sub_model(client: TestClient):
    response = client.get("/model/modelA")
    assert response.status_code == 200, response.text
    assert response.json() == {
        "name": "modelA",
        "description": "model-a-desc",
        "model_b": {"username": "test-user"},
    }


@needs_pydanticv1
def test_validator_is_cloned(client: TestClient):
    with pytest.raises(ResponseValidationError) as err:
        client.get("/model/modelX")
    assert err.value.errors() == [
        {
            "loc": ("response", "name"),
            "msg": "name must end in A",
            "type": "value_error",
        }
    ]


@needs_pydanticv1
def test_openapi_schema(client: TestClient):
    response = client.get("/openapi.json")
    assert response.status_code == 200, response.text
    assert response.json() == {
        "openapi": "3.1.0",
