import logging
import discord from discord import app_commands from peewee import DoesNotExist
from movienightbot.db.controllers import ServerController, VoteController from movienightbot.util import ( add_vote_emojis, build_vote_embed, get_message, )
vote_controller = VoteController() server_controller = ServerController()
logger = logging.getLogger("movienightbot")
async def end_vote_task(interaction: discord.Interaction): server_id = interaction.guild.id with vote_controller.transaction(): try: vote_msg_id = vote_controller.get_by_id(server_id).message_id except DoesNotExist: await interaction.response.send_message("No vote started!") return winning_movies = vote_controller.end_vote(server_id) # TODO: Make more robust so we don't assume the end message and vote message are in same channel # probably safe for now, only happens if admin changes bot channel in the middle of a vote vote_msg = await get_message(interaction.channel, vote_msg_id) if vote_msg: await vote_msg.clear_reactions() else: # Vote message was deleted or is unavailable, so make a new one vote_msg = await interaction.channel.send("replacement vote message") if len(winning_movies) == 1: winning_movie = winning_movies[0].movie_name imdb_info = winning_movies[0].imdb_id if imdb_info: imdb_url = f"https://www.imdb.com/title/tt{imdb_info.imdb_id}/" imdb_markup_link = f"[IMDb Page]({imdb_url})" imdb_year = f"({imdb_info.year})" else: imdb_url, imdb_markup_link, imdb_year = "", "", ""
embed = discord.Embed( title=f"Wining movie: {winning_movie} {imdb_year}", description=f"{imdb_markup_link} Use `/set_watched {winning_movie}` to set the movie as watched", ) await interaction.response.send_message( f"The winning vote was `{winning_movie}`! " f"To set the movie as watched use the command `/set_watched {winning_movie}`", ) await vote_msg.unpin() else: await interaction.response.send_message("There was a tie! Check the vote message for new vote options") with vote_controller.transaction(): server_row = server_controller.get_by_id(server_id) if server_row.tie_option == "breaker": vote_row = vote_controller.start_runoff_vote(server_id, vote_msg, winning_movies) elif server_row.tie_option == "random": vote_row = vote_controller.start_vote(server_id) vote_row.message_id = vote_msg.id vote_row.channel_id = vote_msg.channel.id vote_controller.update(vote_row) embed = build_vote_embed(server_id) await add_vote_emojis(vote_msg, vote_row.movie_votes) await vote_msg.edit(content=None, embed=embed)
@app_commands.command(description="Ends the currently running vote and displays the winning vote.") async def end_vote(interaction: discord.Interaction): await end_vote_task(interaction)
@end_vote.error async def end_vote_error(interaction: discord.Interaction, error: discord.app_commands.errors.CheckFailure): await interaction.response.send_message( "Something went wrong during the execution of this command. I guess you should just go do something else.", ephemeral=True, ) logger.debug(str(error))
async def setup(bot): bot.tree.add_command(end_vote) logger.info("Loaded end_vote command")

import logging import re
import discord from discord import app_commands from peewee import DoesNotExist
from movienightbot.db.controllers import MoviesController, ServerController from movienightbot.util import capitalize_movie_name
logger = logging.getLogger("movienightbot")
class ServerAdmin(app_commands.Group): server_controller = ServerController() movies_controller = MoviesController() time_regex = re.compile(r"^\d{1,2}:\d{2}$")
@app_commands.command( description="Toggles whether to allow tv shows in the IMDB search results (True) or not (False).", ) @app_commands.default_permissions(administrator=True) async def imdb_tv_shows(self, interaction: discord.Interaction, allow_tv_shows: bool): with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.allow_tv_shows = allow_tv_shows self.server_controller.update(server_row) await interaction.response.send_message(f"Allow IMDB tv show search {allow_tv_shows}")
@app_commands.command(description="Toggles allowing suggestions.") @app_commands.default_permissions(administrator=True) async def block_suggestions(self, interaction: discord.Interaction, block: bool): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.block_suggestions = block self.server_controller.update(server_row) suggestions_text = "blocked" if block else "unblocked" await interaction.response.send_message(f"Server suggestions are now {suggestions_text}")
@app_commands.command(description="Toggles checking suggestions against IMDB database before adding.") @app_commands.default_permissions(administrator=True) async def check_movie_names(self, interaction: discord.Interaction, check_names: bool): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.check_movie_names = check_names self.server_controller.update(server_row) option = "on" if check_names else "off" await interaction.response.send_message(f"IMDB movie name checks are now {option}")
@app_commands.command(description="Sets the number of movies that will show up on a vote.") @app_commands.default_permissions(administrator=True) async def movie_option_count( self, interaction: discord.Interaction, num_movies: app_commands.Range[int, 2, 25], ): with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.num_movies_per_vote = int(num_movies) self.server_controller.update(server_row) await interaction.response.send_message(f"Number of movies per vote updated to {num_movies}")
@app_commands.command(description="Removes the specified movie from the suggestions list.") @app_commands.default_permissions(administrator=True) async def remove(self, interaction: discord.Interaction, movie: str): movie = capitalize_movie_name(movie) with self.movies_controller.transaction(): try: movie_row = self.movies_controller.get_by_server_and_id(server_id=interaction.guild.id, movie=movie) except DoesNotExist: await interaction.response.send_message(f"Movie {movie} has not been suggested", ephemeral=True) return self.movies_controller.delete(movie_row)
await interaction.response.send_message(f"The movie {movie} has been removed from the list.")
def _format_server_embed(self, client: discord.Client, server_id: int) -> discord.Embed: ignore_attrs = {"id"} server_row = self.server_controller.get_by_id(server_id) guild_name = client.get_guild(server_row.id) embed = discord.Embed( title=f"{guild_name} Settings", description=f"Current settings for server `{guild_name}`", ) for attr, value in server_row.__data__.items(): if attr in ignore_attrs: continue if "time" in attr and "timeout" not in attr: value = str(value) + " UTC" embed.add_field(name=attr, value=value, inline=False) return embed
@app_commands.command(description="View the current server settings") @app_commands.default_permissions(administrator=True) async def server_settings(self, interaction: discord.Interaction): embed_data = self._format_server_embed(interaction.client, interaction.guild.id) await interaction.response.send_message(content=None, embed=embed_data)
@app_commands.command( description="Sets the role allowed to run admin commands. Server administrators have admin by default", ) @app_commands.default_permissions(administrator=True) async def set_admin_role(self, interaction: discord.Interaction, role: str): server_roles = [r.name for r in interaction.guild.roles] if role not in server_roles: await interaction.response.send_message(f"Role not found: {role}.", ephemeral=True) return with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.admin_role = role self.server_controller.update(server_row) await interaction.response.send_message(f"Admin role updated to {role}")
@app_commands.command( description="How long before suggestion messages are deleted, in seconds. Set to 0 to disable.", ) @app_commands.default_permissions(administrator=True) async def set_message_timeout( self, interaction: discord.Interaction, timeout: app_commands.Range[int, 0, None], ): with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.message_timeout = timeout self.server_controller.update(server_row) await interaction.response.send_message(f"Message timeout updated to {timeout} seconds")
@app_commands.command(description="Sets the time when the movie will be watched in 24 hour UTC time.") @app_commands.default_permissions(administrator=True) async def set_movie_time(self, interaction: discord.Interaction, movie_time: str): if not self.time_regex.search(movie_time): await interaction.response.send_message( "Movie time given in invalid format. Must be `HH:MM`", ephemeral=True, ) return with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.movie_time = movie_time self.server_controller.update(server_row) await interaction.response.send_message(f"Movie time updated to {movie_time} UTC")
@app_commands.command( description="Sets how the bot handles tied votes. `breaker`: revote with ties (default). `random`: new vote", ) @app_commands.default_permissions(administrator=True) async def tie_option(self, interaction: discord.Interaction, tie_option: str): tie_options = {"breaker", "random"} if tie_option not in tie_options: await interaction.response.send_message( f"Unknown tiebreaker option given: {tie_option}. Must be one of {''.join(tie_options)}", ephemeral=True, ) return with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) server_row.tie_option = tie_option self.server_controller.update(server_row) await interaction.response.send_message(f"Tiebreaker updated to {tie_option}")
@app_commands.command(description="Number of movies a user can vote for.") @app_commands.default_permissions(administrator=True) async def user_vote_count( self, interaction: discord.Interaction, num_votes_per_user: app_commands.Range[int, 1, 25], ): with self.server_controller.transaction(): server_row = self.server_controller.get_by_id(interaction.guild.id) if num_votes_per_user > server_row.num_movies_per_vote: await interaction.response.send_message( f"Failed to update: Number of votes per user must be <= {server_row.num_movies_per_vote}", ephemeral=True, ) return server_row.num_votes_per_user = int(num_votes_per_user) self.server_controller.update(server_row) await interaction.response.send_message(f"Number of votes per user updated to {num_votes_per_user}")
async def setup(bot): bot.tree.add_command(ServerAdmin(name="server", description="Admin commands for MovieNightBot server setup")) logger.info("Loaded server commands")
