"""Tests for using subprocesses in tests."""
from __future__ import annotations
import asyncio.subprocess import sys
import pytest
if sys.platform == "win32": # The default asyncio event loop implementation on Windows does not # support subprocesses. Subprocesses are available for Windows if a # ProactorEventLoop is used. @pytest.fixture() def event_loop(): loop = asyncio.ProactorEventLoop() yield loop loop.close()
@pytest.mark.asyncio async def test_subprocess(): """Starting a subprocess should be possible.""" proc = await asyncio.subprocess.create_subprocess_exec( sys.executable, "--version", stdout=asyncio.subprocess.PIPE ) await proc.communicate()

from __future__ import annotations

import asyncio
import unittest.mock

import pytest

START = object()
END = object()
RETVAL = object()


@pytest.fixture
def mock():
    return unittest.mock.Mock(return_value=RETVAL)


@pytest.fixture
async def async_fixture(mock):
    return await asyncio.sleep(0.1, result=mock(START))


@pytest.mark.asyncio
async def test_async_fixture(async_fixture, mock):
    assert mock.call_count == 1
    assert mock.call_args_list[-1] == unittest.mock.call(START)
    assert async_fixture is RETVAL


class TestAsyncFixtureMethod:
    is_same_instance = False

    @pytest.fixture(autouse=True)
    async def async_fixture_method(self):
        self.is_same_instance = True

    @pytest.mark.asyncio
    async def test_async_fixture_method(self):
        assert self.is_same_instance

""" Regression test for https://github.com/pytest-dev/pytest-asyncio/issues/127: contextvars were not properly maintained among fixtures and tests. """
from __future__ import annotations
import sys from textwrap import dedent
import pytest from pytest import Pytester
_prelude = dedent( """ import pytest import pytest_asyncio from contextlib import contextmanager from contextvars import ContextVar
_context_var = ContextVar("context_var")
@contextmanager def context_var_manager(value): token = _context_var.set(value) try: yield finally: _context_var.reset(token) """ )
def test_var_from_sync_generator_propagates_to_async(pytester: Pytester): pytester.makeini("[pytest]\nasyncio_default_fixture_loop_scope = function") pytester.makepyfile( _prelude + dedent( """ @pytest.fixture def var_fixture(): with context_var_manager("value"): yield
@pytest_asyncio.fixture async def check_var_fixture(var_fixture): assert _context_var.get() == "value"
@pytest.mark.asyncio async def test(check_var_fixture): assert _context_var.get() == "value" """ ) ) result = pytester.runpytest("--asyncio-mode=strict") result.assert_outcomes(passed=1)
@pytest.mark.xfail( sys.version_info < (3, 11), reason="requires asyncio Task context support", strict=True, ) def test_var_from_async_generator_propagates_to_sync(pytester: Pytester): pytester.makeini("[pytest]\nasyncio_default_fixture_loop_scope = function") pytester.makepyfile( _prelude + dedent( """ @pytest_asyncio.fixture async def var_fixture(): with context_var_manager("value"): yield
@pytest.fixture def check_var_fixture(var_fixture): assert _context_var.get() == "value"
@pytest.mark.asyncio async def test(check_var_fixture): assert _context_var.get() == "value" """ ) ) result = pytester.runpytest("--asyncio-mode=strict") result.assert_outcomes(passed=1)
@pytest.mark.xfail( sys.version_info < (3, 11), reason="requires asyncio Task context support", strict=True, ) def test_var_from_async_fixture_propagates_to_sync(pytester: Pytester): pytester.makeini("[pytest]\nasyncio_default_fixture_loop_scope = function") pytester.makepyfile( _prelude + dedent( """ @pytest_asyncio.fixture async def var_fixture(): _context_var.set("value") # Rely on async fixture teardown to reset the context var.
