message = await c.send("Test Message") await dpytest.add_reaction(m, message, "")
# Assumes the above tests pass message = await c.fetch_message(message.id) react = message.reactions[0] assert react.emoji == "" assert react.me is False
@pytest.mark.asyncio async def test_user_remove_reaction(bot): g = bot.guilds[0] c = g.text_channels[0] m = g.members[0]
message = await c.send("Test Message") await message.add_reaction("") await dpytest.add_reaction(m, message, "") await dpytest.remove_reaction(m, message, "")
# Assumes the above tests pass message = await c.fetch_message(message.id) react = message.reactions[0] assert react.emoji == "" assert react.count == 1 assert react.me is True

