from discord.ext.commands import Cog, command
class Echo(Cog):
# Silence the default on_error handler async def cog_command_error(self, ctx, error): pass
@command() async def echo(self, ctx, *, text: str): await ctx.send(text)
async def setup(bot): await bot.add_cog(Echo())

from discord.ext import commands


class Greeting(commands.Cog):

    @commands.Cog.listener()
    async def on_member_join(self, member):
        channel = member.guild.text_channels[0]
        if channel is not None:
            await channel.send(f"Welcome {member.mention}.")


async def setup(bot):
    await bot.add_cog(Greeting())

.function, .method {
    margin-bottom: 40px;
}
